import React from "react";
import Card from "react-bootstrap/Card";
import { FaGooglePlay } from "react-icons/fa";
import { AiFillApple } from "react-icons/ai";
import Button from "react-bootstrap/Button";

function Landing() {
  return (
    <Card.Body style={CardStyle}>
      <Card.Title>
        <h1 style={H1Style}>Be truly borderless</h1>
      </Card.Title>
      <Card.Text>
        <p style={PStyle}>
          Send and receive money globally. Create virtual dollar cards that work
          anywhere online.
        </p>
      </Card.Text>
      <Card.Text style={Buttons}>
        <Button style={ButtonStyle}>
          <FaGooglePlay style={{ fontSize: "30px" }} /> Get it on Play Store
        </Button>
        <Button style={ButtonStyle}>
          <AiFillApple style={{ fontSize: "30px" }} /> Get it on Apple Store
        </Button>
      </Card.Text>
    </Card.Body>
  );
}

const ButtonStyle = {
  borderRadius: "3px",
  color: "inherit",
  display: "inline-block",
  width: "170px",
  height: "50px",
  border: "2px solid #333",
  cursor: "pointer",
  backgroundColor: "transparent",
  textAlign: "center",
  display: "flex",
};
const H1Style = {
  fontSize: "60px",
  lineHeight: "72px",
  // marginBottom: "15px",
  width: "100%",
  height: "100px",
  border: "2px solid black",
};
const PStyle = {
  fontSize: "40px",
  lineHeight: "40px",
  // marginBottom: "50px",
  width: "100%",
  height: "150px",
  border: "2px solid black",
};
const CardStyle = {
  width: "100%",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  // justifyContent: "center",
  // border: "2px solid black",
  height: "auto",
  fontWeight: "400",
  color: "#1d222c",
  minHeight: "100vh",
};
const Buttons = {
  display: "flex",
  gap: "30px",
};
export default Landing;
